import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { TextInput, StyleSheet, View, Text, Button } from 'react-native';


import Hello from './Hello'

export default class App extends React.Component {
  constructor() {
    super();
    this.state = { 
      inputValue: '',
      tip: 0.2
    }
  }

  render() {
    let tip = 0.00;
    if (this.state.inputValue) {
      tip = parseFloat(this.state.inputValue) * this.state.tip;
      tip = (Math.round(tip * 100) / 100).toFixed(2);
    }

    return (
      <View style={styles.container}>
        <Text>
          ${tip}
        </Text>
        <TextInput
          value={ this.state.inputValue }
          style={styles.textInput}
          keyboardType='numeric'
          placeholder='0.00'
          onChangeText={(text)=>this.setState({inputValue: text})}
        />
        <View style={styles.tipList}>
          <Button title='10%' style={styles.tipButton}>10%</Button>
          <Button title='15%' style={styles.tipButton}>15%</Button>
          <Button title='20%' style={styles.tipButton}>20%</Button>
          <Button title='Custom'>Custom</Button>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    padding:20,

  },
  textInput: {
    height: 60,
    fontSize:20,
    borderColor: '#333',
    borderWidth: 1,
    width: '100%',
    padding:10,
  },
  tipList: {
    flex: '50%',
    flexDirection: 'row',
    height: 20,
    padding: 30,
    height: '20%',
  },
  tipButton: {
    flex: 1,

    fontSize: 600,
    height: 20,
    padding: '20%',
  }

});
